import 'package:dio/dio.dart';import 'package:flutter/foundation.dart';import 'package:state_management_app/isolate_task/get_all_post.dart';import 'package:state_management_app/isolate_task/worker/worker.dart';import 'package:state_management_app/model/allpost_model.dart';class AllPostBloc extends ChangeNotifier {  List<AllPost> allPost;  bool loading = false;  Future getTodoData() async {    final dio = Dio();    var todoTask = GetAllPost(dio: dio);    final worker = Worker(poolSize: 2);    loading = true;    notifyListeners();    allPost = (await worker.handle(todoTask)) as List<AllPost>;    loading = false;    notifyListeners();  }}