import 'package:dio/dio.dart';import 'package:flutter/foundation.dart';import 'package:state_management_app/isolate_task/get_comment.dart';import 'package:state_management_app/isolate_task/worker/worker.dart';import 'package:state_management_app/model/comment_model.dart';class CommentBloc extends ChangeNotifier {  List<Comment> comment;  bool loading = false;  Future getTodoComment(int id) async {    final dio = Dio();    var todoTask = GetComment(dio: dio, id: id);    final worker = Worker(poolSize: 2);    loading = true;    notifyListeners();    comment = (await worker.handle(todoTask)) as List<Comment>;    loading = false;    notifyListeners();  }}