import 'package:dio/dio.dart';import 'package:flutter/foundation.dart';import 'package:state_management_app/isolate_task/get_all_post.dart';import 'package:state_management_app/isolate_task/get_detail.dart';import 'package:state_management_app/isolate_task/worker/worker.dart';import 'package:state_management_app/model/allpost_model.dart';import 'package:state_management_app/model/detail_model.dart';class DetailBloc extends ChangeNotifier {  Detail detail;  bool loading = false;  Future getTodoDetail(int id) async {    final dio = Dio();    var todoTask = GetDetail(dio: dio, id: id);    final worker = Worker(poolSize: 2);    loading = true;    notifyListeners();    detail = (await worker.handle(todoTask)) as Detail;    loading = false;    notifyListeners();  }}