import 'dart:async';import 'package:dio/dio.dart';import 'package:flutter/foundation.dart';import 'package:state_management_app/model/allpost_model.dart';import 'package:state_management_app/model/comment_model.dart';import 'worker/worker.dart';class GetComment implements Task<Future<List<Comment>>> {  final Dio dio;  final int id;  GetComment({@required this.dio, @required this.id});  @override  Future<List<Comment>> execute() {    return _doExecute();  }  Future<List<Comment>> _doExecute() async {    var completer = Completer<List<Comment>>();    try {      Response response = await dio.get(          'https://jsonplaceholder.typicode.com/comments?postId=$id');      print(' DATAAAAAAAAAA ${response.data.toString()}');      List<Comment> todoModel = List<Comment>.from((response.data).map((x) => Comment.fromJson(x)));      print('GetAllPost is DONE result=$todoModel');      completer.complete(todoModel);    } catch (e) {      print('GetAllPost error: $e');      completer.completeError(e);    }    return completer.future;  }}