import 'dart:async';import 'package:dio/dio.dart';import 'package:flutter/foundation.dart';import 'package:state_management_app/model/detail_model.dart';import 'worker/worker.dart';class GetDetail implements Task<Future<Detail>> {  final Dio dio;  final int id;  GetDetail({@required this.dio, @required this.id});  @override  Future<Detail> execute() {    return _doExecute();  }  Future<Detail> _doExecute() async {    var completer = Completer<Detail>();    try {      var response = await dio.get<Map<String, dynamic>>(          'https://jsonplaceholder.typicode.com/posts/$id');      var todoModel = Detail.fromJson(response.data);      print('GetDetail is DONE result=$todoModel');      completer.complete(todoModel);    } catch (e) {      print('GetDetail error: $e');      completer.completeError(e);    }    return completer.future;  }}