import 'dart:convert';Detail detailFromJson(String str) => Detail.fromJson(json.decode(str));String detailToJson(Detail data) => json.encode(data.toJson());class Detail {  int userId;  int id;  String title;  String body;  Detail({    this.userId,    this.id,    this.title,    this.body,  });  factory Detail.fromJson(Map<String, dynamic> json) => Detail(    userId: json["userId"],    id: json["id"],    title: json["title"],    body: json["body"],  );  Map<String, dynamic> toJson() => {    "userId": userId,    "id": id,    "title": title,    "body": body,  };}