import 'package:flutter/material.dart';import 'package:provider/provider.dart';import 'package:state_management_app/blocs/detail_bloc.dart';import 'package:state_management_app/model/allpost_model.dart';import 'package:state_management_app/model/detail_model.dart';class DetailPage extends StatefulWidget {  final AllPost allPost;  const DetailPage({Key key, this.allPost}) : super(key: key);  @override  _DetailPageState createState() => _DetailPageState();}class _DetailPageState extends State<DetailPage> {  @override  void initState() {    print('INI ID = ${widget.allPost.id}');    super.initState();    Provider.of<DetailBloc>(context, listen: false).getTodoDetail(widget.allPost.id);  }  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        actions: <Widget>[          IconButton(            icon: Icon(              Icons.edit,              color: Colors.black,            ),            onPressed: () {},          )        ],        centerTitle: true,        title: Text(          'DETAIL POST',          style: TextStyle(color: Colors.black),        ),        backgroundColor: Colors.white,        leading: IconButton(          icon: Icon(            Icons.arrow_back,            color: Colors.black,          ),          onPressed: () {            Navigator.pop(context);          },        ),      ),      body: Consumer(          builder: (BuildContext context, DetailBloc detailbl, Widget child) {        Detail value ;        if (detailbl.loading) {          return CircularProgressIndicator();        } else {          if (detailbl.detail != null) {            value = detailbl.detail;          }        }        return Column(          crossAxisAlignment: CrossAxisAlignment.start,          children: <Widget>[            Container(              margin: EdgeInsets.only(left: 20, top: 20),              child: Text(                'Name : ${value.title.toUpperCase()}',                style: TextStyle(fontWeight: FontWeight.bold),              ),            ),            Container(              margin: EdgeInsets.only(left: 20, top: 20),              child: Text('Body : ${value.body}'),            ),            Container(              margin: EdgeInsets.only(left: 10, top: 10),              child: Text(                "Comment : ",                style: TextStyle(fontWeight: FontWeight.bold, fontSize: 20),              ),            ),            Container(            )          ],        );      }),    );  }}